css:
.datas {
    text-align: center;
    border: 1px solid black; 
    font-size:12px;
}
.headings {
    text-align: center;
    border: 1px solid black; 
    font-size:14px;
    font-weight:600;
}
table{
    width:98%;
}
td{
    line-height:0;
    page-break-inside:avoid !important;
}

format_data:
[{"fieldname": "print_heading_template", "fieldtype": "Custom HTML", "options": "<div></div>"}, {"fieldtype": "Section Break", "label": ""}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "<div class="datas">
    <table>
        <h3>Stiching Combination</h3>
        <td>
            <table>
                <tr>
                    <td class="headings">
                        {{doc.stiching_attribute}}
                    </td>
                    {% if doc.is_set_item %}
                        <td class="headings">
                            {{doc.set_item_attribute}}
                        </td>
                    {% endif %}
                    <td class="headings">
                        Quantity
                    </td>
                </tr>
                {% for row in doc.stiching_item_details %}
                    <tr>
                        <td class="datas">
                            {{row.stiching_attribute_value}}
                        </td>
                        {% if doc.is_set_item %}
                            <td class="datas">
                                {{row.set_item_attribute_value}}
                            </td>
                        {% endif %}
                        <td class="datas">
                            {{row.quantity}}
                        </td>
                    </tr>
                {% endfor %}
            </table>
        </td>
        <td>
            <table>
                <tr>
                    <td class="headings">{{doc.packing_attribute}}</td>
                </tr>
                {% for row in doc.packing_attribute_details %}
                    <tr>
                        <td class="datas">{{row.attribute_value}}</td>
                    </tr>
                {% endfor %}
            </table>
        </td>    
    </table>    
    {% set stiching_combination = fetch_combination_items(doc.stiching_item_combination_details)%}
    <h4>Colour Combination</h4>
    <table style="margin:6px;">
        <tr>
            {% for head in stiching_combination['attributes'] %}
                <td class="headings">{{head}}</td>
            {% endfor %}
        </tr>
        {% for value in stiching_combination['values'] %}
            <tr>
                {% for val in value['val'] %}
                    <td class="datas">{{ value['val'][stiching_combination['attributes'][loop.index - 1]] }}</td>
                {% endfor %}
            </tr>    
        {% endfor %}
    </table>
    <h3>Cutting Detail</h3>
    {% set ns = namespace(attrs=[]) %}
    {% for attr in doc.item_attributes %}
        {% if attr.attribute != doc.dependent_attribute %}
            {% set ns.attrs = ns.attrs + [attr.attribute] %}
        {% endif %}    
    {% endfor %}
    {% set cutting_items = get_dict_object(doc.cutting_items_json) %}
    {% set item_keys = cutting_items['items'][0] %}
    <table style="margin:6px;">
        <tr>
            {% for key, value in item_keys.items() %}
                <td class="headings"> {{key}} </td>
            {% endfor %}
        </tr>
        {% for item in cutting_items['items'] %}
            <tr>
                {% for key, value in item_keys.items() %}
                    <td class="datas"> {{item[key]}} </td>
                {% endfor %}
            </tr>
        {% endfor %}
    </table>    
    <h3>Accessory Detail</h3>
    {% set accessory_items = get_dict_object(doc.cloth_accessory_json) %}
    {% set item_keys = accessory_items['items'][0] %}
    <table style="margin:6px;">
        <tr>
            {% for key, value in item_keys.items() %}
                <td class="headings"> {{key}} </td>
            {% endfor %}
        </tr>
        {% for item in accessory_items['items'] %}
            <tr>
                {% for key, value in item_keys.items() %}
                    <td class="datas"> {{item[key]}} </td>
                {% endfor %}
            </tr>
        {% endfor %}
    </table>    
</div>"}]
