                            <td>
                                {{row.set_item_attribute_value}}
                            </td>
                        {% endif %}
                        <td>
                            {{row.quantity}}
                        </td>
                    </tr>
                {% endfor %}
            </table>
        </td>
        <td>
            <table>
                <tr>
                    <th>{{doc.packing_attribute}}</th>
                </tr>
