css:
.datas {
    text-align: center;
    border: 1px solid black; 
    font-size:12px;
}
.headings {
    text-align: center;
    border: 1px solid black; 
    font-size:14px;
    font-weight:600;
}
table{
    width:98%;
}
td{
    line-height:1;
    page-break-inside:avoid !important;
}
#pageborder {
    position:fixed;
    left: 0;
    right: 0;
    top: 0;
    bottom: 0;
    border: 2px solid black;
    padding:5px;
}
h3{
    padding-left:10px;
}

format_data:
[{"fieldname": "print_heading_template", "fieldtype": "Custom HTML", "options": "<div></div>"}, {"fieldtype": "Section Break", "label": ""}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "<div>
    <div id="pageborder"></div>
    {% set ipd_doc = get_ipd_pf_details(doc.production_detail) %}
    {% set emblishment = get_dict_object(ipd_doc.emblishment_details_json)%}
    <div style="padding-top:10px;width:100%;display: flex;">
        <div style="font-size:16px;margin-left:20px;font-weight:600;width:50%;text-align: left;">
            Lot  : <span>{{doc.name}}</span>
        </div>
        <div style="font-size:16px;margin-left:20px;font-weight:600;width:50%;text-align: right;padding-right:10px;">
            Item : <span>{{doc.item}}</span>
        </div>
    </div>
    <table style="margin:0px 20px 0px 20px;width:95%;">
        <h3 style="text-decoration: underline;margin-left: 10px ;">Stiching Combination</h3>
        <td>
            <table>
                <tr>
                    {% if ipd_doc.is_set_item %}
                        <td class="headings">
                            {{ipd_doc.set_item_attribute}}
                        </td>
                    {% endif %}
                    <td class="headings">
                        {{ipd_doc.stiching_attribute}}
                    </td>
                    <td class="headings">
                        Quantity
                    </td>
                    {% for emb in emblishment %}
                        <td class="headings">
                            {{emb}}
                        </td>
                    {% endfor %}
                </tr>
                {% for row in ipd_doc.stiching_item_details %}
                    <tr>
                        {% if ipd_doc.is_set_item %}
                            <td class="datas">
                                {{row.set_item_attribute_value}}
                            </td>
                        {% endif %}
                        <td class="datas">
                            {{row.stiching_attribute_value}}
                        </td>
                        <td class="datas">
                            {{row.quantity}}
                        </td>
                        {% for emb in emblishment %}
                            <td class="datas">
                                {% if row.stiching_attribute_value in emblishment[emb] %}
                                    <svg xmlns="http://www.w3.org/2000/svg" width="15" height="15" viewBox="0 0 24 24">
                                        <path fill="#000" d="M21 7L9 19l-5.5-5.5l1.41-1.41L9 16.17L19.59 5.59z" />
                                    </svg>
                                {% endif %}
                            </td>
                        {% endfor %}
                    </tr>
                {% endfor %}
            </table>
        </td>
    </table>    
    {% set stiching_combination = fetch_combination_items(ipd_doc.stiching_item_combination_details)%}
    {% set set_attr_panels = {} %}
    {% if ipd_doc.is_set_item %}
        {% for row in ipd_doc.stiching_item_details %}
            {% if set_attr_panels[row.set_item_attribute_value] %}
                {% set val = set_attr_panels[row.set_item_attribute_value] + 1 %}
                {% if set_attr_panels.update({row.set_item_attribute_value:val}) %}{% endif %}
            {% else %}
                {% set _ = set_attr_panels.update({row.set_item_attribute_value:1}) %}
            {% endif %}
        {% endfor %}
    {% endif %}
    <h3 style="text-decoration: underline;margin-left: 10px ;">Colour Combination</h3>
    <table style="margin:0px 20px 0px 20px;width:95%;">
        {% if ipd_doc.is_set_item %}
            <tr>
                {% for key, val in set_attr_panels.items() %}
                    <td class="headings" colspan="{{val}}"> {{key}} </td>
                {% endfor %}
            </tr>
        {% endif %}
        <tr>
            {% for head in stiching_combination['attributes'] %}
                <td class="headings">{{head}}</td>
            {% endfor %}
        </tr>
        {% for value in stiching_combination['values'] %}
            <tr>
                {% for val in value['val'] %}
                    <td class="datas">{{ value['val'][stiching_combination['attributes'][loop.index - 1]] }}</td>
                {% endfor %}
            </tr>    
        {% endfor %}
    </table>
    {% set accessories = get_dict_object(ipd_doc.stiching_accessory_json) %}
    {% set accessory_json = get_dict_object(ipd_doc.accessory_clothtype_json) %}
    {% set accessory_attr_detail = {} %}
    {% if accessory_json %}
       {% set acc = get_ipd_print_accessory_combination(doc.production_detail) %}
       {% set stich_acc = get_dict_object(acc) %}
        <h3 style="text-decoration: underline;margin-left: 10px ;">Cloth Accessory Combination</h3>
        {% if ipd_doc.is_set_item %}
            {% for accessory, part in accessory_json.items() %}
                {% if accessory_attr_detail.get(part) %}
                    {% set _ = accessory_attr_detail[part].append(accessory) %}
                {% else %}
                    {% set accessory_attr_detail = accessory_attr_detail.update({part: [accessory]}) %}
                {% endif %}
            {% endfor %}
        {% endif %}
        {% if ipd_doc.is_set_item %}
            <table style="margin:0px 20px 0px 20px;width:95%;">
                <tr>
                    {% for part, accessory in accessory_attr_detail.items() %}
                        {% set len = accessory|length * 3 %}
                        <td style="padding-right: 0px !important;padding-left: 0px !important;">
                            <table>
                                <tr>
                                    <td class="headings" colspan="{{len}}">{{part}}</td>
                                </tr>
                                <tr>
                                    {% for part2, accessory in accessory_attr_detail.items() %}
                                        {% if part2 == part %}
                                            {% for acc in accessory %}
                                            <td class="headings" colspan="3">{{acc}}</td>
                                            {% endfor %}
                                        {% endif %}   
                                    {% endfor %}
                                </tr>
                                {% for major in stich_acc[part] %}
                                    <tr>
                                    {% for part2, accessory in accessory_attr_detail.items() %}
                                        {% if part2 == part %}
                                            {% for acc in accessory %}
                                                <td class="headings">{{major}}</td>
                                                <td class="datas">{{stich_acc[part][major][acc]['colour']}}</td>
                                                <td class="datas">{{stich_acc[part][major][acc]['cloth_type']}}</td>
                                            {% endfor %}
                                        {% endif %}   
                                    {% endfor %}
                                    </tr>
                                {% endfor %}
                            </table>
                        </td>    
                    {% endfor %}
                </tr>    
            </table>
        {% else %}
            <table style="margin:0px 20px 0px 20px;width:95%;">
                <tr>
                    {% for accessory, part in accessory_json.items() %}
                        <td class="headings" colspan="3">{{accessory}}</td>
                    {% endfor %}
                </tr>   
                {% for colour in stich_acc %}
                    <tr>
                        {% for accessory, part in accessory_json.items() %}
                            <td class="headings">{{colour}}</td>
                            <td class="datas">{{stich_acc[colour][accessory]['colour']}}</td>
                            <td class="datas">{{stich_acc[colour][accessory]['cloth_type']}}</td>
                        {% endfor %}
                    </tr>
                {% endfor %}
            </table>    
        {% endif %}
    {% endif %}    
 </div>
 "}]
