css:
#background {
	position: absolute;
	z-index: 0;
	min-height: 143%;
	min-width: 93%;
}

#content {
	z-index: 1;
}

#bg-text {
	color: black;
	font-size: 120px;
	transform: rotate(315deg);
	-webkit-transform: rotate(315deg);
	opacity: 24%;
	text-align: center;
}

#small-bg-text {
	font-size: 20px;
}

format_data:
[{"fieldname": "print_heading_template", "fieldtype": "Custom HTML", "options": "<div></div>"}, {"fieldtype": "Section Break", "label": ""}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "<div style="border:1px solid #aaa; border-radius: 0px; border-collapse: collapse;" class="container-fluid">
    {% if doc.docstatus == 0 or doc.docstatus == 2 %}
    <div id="background">
        <p id="bg-text">
            {% if doc.docstatus == 0 %}
            Draft
            <br> 
            <span class="small-bg-text" style="font-size: 20px;">This Document is pending for approval</span>
            {% else %}
            Cancelled
            <br> 
            <span class="small-bg-text" style="font-size: 20px;">This Document is Cancelled</span>
            {% endif %}
        </p>
    </div>
    {% endif %}
    {% if doc.purpose in ['Send to Warehouse', 'Material Receipt', 'Material Issue'] %}
    <div id="content">
        <div class="row" style="border-bottom:1px solid #666;display: flex;">
            {% if doc.purpose in ['Send to Warehouse', 'Material Issue'] %}
                <div class="col-xs-4" style="border-right:1px solid #ddd;">
                    <div class="col-xs-12" style="font-size:95%; padding-left: 0;">
                        <small>From:</small>
                        {% set from_warehouse = frappe.db.get_value("Supplier", doc.from_warehouse, "supplier_name") %}
                        <br><strong>{{from_warehouse}}</strong>
                        {% set from_address = get_supplier_address_display(doc.from_warehouse) %}
                        {% if from_address %}
                        <br>{{from_address}}
                        {% endif %}
                    </div>
                </div>
            {% elif doc.transfer_supplier %}
                <div class="col-xs-4" style="border-right:1px solid #ddd;">
                    <div class="col-xs-12" style="font-size:95%; padding-left: 0;">
                        <small>From:</small>
                        {% set from_warehouse = frappe.db.get_value("Supplier", doc.transfer_supplier, "supplier_name") %}
                        <br><strong>{{from_warehouse}}</strong>
                        {% set from_address = get_supplier_address_display(doc.transfer_supplier) %}
                        {% if from_address %}
                        <br>{{from_address}}
                        {% endif %}
                    </div>
                </div>
            {% endif %}  
            {% if doc.purpose in ['Send to Warehouse', 'Material Receipt'] %}  
                <div class="col-xs-4" style="border-right:1px solid #ddd;">
                    <div class="col-xs-12" style="font-size:95%; padding-left: 0;">
                        <small>To:</small>
                        {% set to_warehouse = frappe.db.get_value("Supplier", doc.to_warehouse, "supplier_name") %}
                        <br><strong>{{to_warehouse}}</strong>
                        {% set to_address = get_supplier_address_display(doc.to_warehouse) %}
                        {% if to_address %}
                        <br>{{to_address}}
                        {% endif %}
                    </div>
                </div>
            {% elif doc.transfer_supplier %}
                <div class="col-xs-4" style="border-right:1px solid #ddd;">
                    <div class="col-xs-12" style="font-size:95%; padding-left: 0;">
                        <small>To:</small>
                        {% set to_warehouse = frappe.db.get_value("Supplier", doc.transfer_supplier, "supplier_name") %}
                        <br><strong>{{to_warehouse}}</strong>
                        {% set to_address = get_supplier_address_display(doc.transfer_supplier) %}
                        {% if to_address %}
                        <br>{{to_address}}
                        {% endif %}
                    </div>
                </div>
            {% endif %}
            <div class="col-xs-4" style="border-left:1px solid #ddd;">
                <div class="row" style="border-bottom:1px solid #ddd;">
                    <div class="col-xs-12 text-center" style="padding-left:0px;"><strong>Delivery Challan</strong></div>
                </div>
                <div class="row" style="border-bottom:1px solid #ddd;">
                    <div class="col-xs-5 text-right" style=" padding-right: 4px; padding-left:0px;"><small>Date:</small></div>
                    <div class="col-xs-7" style="padding-left:0px;"><strong>{{doc.get_formatted("posting_date")}}</strong></div>
                </div>
                <div class="row" style="border-bottom:1px solid #ddd;">
                    <div class="col-xs-5 text-right" style=" padding-right: 4px; padding-left:0px;"><small>No.:</small></div>
                    <div class="col-xs-7" style="padding-left:0px;"><strong>{{doc.name}}</strong></div>
                </div>
                <div class="row" style="border-bottom:1px solid #ddd;">
                    <div class="col-xs-5 text-right" style=" padding-right: 4px; padding-left:0px;"><small>Process:</small></div>
                    <div class="col-xs-7" style="padding-left:0px;"><strong>Transfer</strong></div>
                </div>
                {% if doc.vehicle_no %}
                    <div class="row" style="border-bottom:1px solid #ddd;">
                        <div class="col-xs-5 text-right" style=" padding-right: 4px; padding-left:0px;"><small>Vehicle No:</small></div>
                        <div class="col-xs-7" style="padding-left:0px;"><strong>{{ doc.vehicle_no }}</strong></div>
                    </div>
                {% endif %}    
            </div>
        </div>
        <div class="row" style="border-bottom:1px solid #666;">
            <div class="col-xs-12 text-center">Particulars</div>
        </div>
        <div class="row">
            {% set item_details = fetch_stock_entry_items(doc.items) %}
            <table class="table table-sm table-bordered">
                {% for i in item_details %}
                <tr>
                    {% if i.primary_attribute %}
                    <td>
                        <table class="table table-sm table-bordered">
                            <tr>
                                <th width="25">S.No.</th>
                                <th>Item</th>
                                <th>Lot</th>
                                {% for attr in i['attributes'] %}
                                <th>{{ attr }}</th>
                                {% endfor %}
                                {% for attr in i['primary_attribute_values'] %}
                                <th>{{ attr }}</th>
                                {% endfor %}
                            </tr>
                            {% for j in i['items'] %}
                            <tr>
                                <td>{{ loop.index }}</td>
                                <td>{{ j.name }}</td>
                                <td>{{ j.lot }}</td>
                                {% for attr in i['attributes'] %}
                                <td>
                                    {% if attr in j.attributes %}
                                        {{ j.attributes[attr] }}
                                    {% endif %}
                                </td>
                                {% endfor %}
                                {% for attr in j['values'] %}
                                <td>
                                    {% if j['values'][attr].qty %}
                                    <div>
                                        {{ j['values'][attr].qty }}{% if j.default_uom %}{{' ' + j.default_uom}}{% endif %}
                                        <!-- <br>
                                        Rate: {{ frappe.utils.fmt_money(j['values'][attr].rate, currency="₹") }} -->
                                    </div>
                                    {% else %}
                                    <div class="text-center">
                                        ---
                                    </div>
                                    {% endif %}
                                </td>
                                {% endfor %}
                            </tr>
                            {% endfor %}
                        </table>
                    </td>
                    {% else %}
                    <td>
                        <table class="table table-sm table-bordered">
                            <tr>
                                <th width="25">S.No.</th>
                                <th>Item</th>
                                <th>Lot</th>
                                {% for attr in i.attributes %}
                                    <th>{{ attr }}</th>
                                {% endfor %}
                                <th>Quantity</th>
                                <!-- <th>Rate</th> -->
                            </tr>
                            {% for j in i['items'] %}
                            <tr>
                                <td>{{ loop.index }}</td>
                                <td>{{ j.name }}</td>
                                <td>{{ j.lot }}</td>
                                {% for attr in i['attributes'] %}
                                    <td>{{ j.attributes[attr] }}</td>
                                {% endfor %}
                                <td>
                                    {{ j['values']['default'].qty }}{% if j.default_uom %}{{' ' + j.default_uom}}{% endif %}
                                </td>
                                <!-- <td>
                                    {{ frappe.utils.fmt_money(j['values']['default'].rate, currency="₹") }}
                                </td> -->
                            </tr>
                            {% endfor %}
                        </table>
                    </td>
                    {% endif %}
                </tr>
                {% endfor %}
            </table>
        </div>
        <div class="row" style="border-bottom:1px solid #666;border-top:1px solid #666;">
            <div class="col-xs-12">Estimated Value: {{ frappe.utils.fmt_money(doc.total_amount, currency="₹") }}</div>
        </div>
        <div class="row">
            {% set company = frappe.db.get_single_value("Company Settings", "company") %}
            <div class="col-xs-6 text-center" style="border-right:1px solid #666;">
                Receiver's Signature
                <br>
                <br>
                <br>
                <div class="row text-left">
                    <div class="col-xs-6">
                        Name:
                    </div>
                    <div class="col-xs-6">
                        Designation:
                    </div>
                </div>
            </div>
            <div class="col-xs-6 text-right">
                <small>For </small>
                <strong>{{ company  }}</strong>
                <br>
                <br>
                <br>
                Authorized Signatory
                <!-- {% set signature = get_user_signature(doc.approved_by) %}
                {% if signature %}
                <div>
                    <img src={{signature}} style="height: 50px;">
                    <br>Authorized Signatory
                </div>
                {% else %} -->
                <!-- {% endif %} -->
            </div>
        </div>
    </div>
    {% endif %}
</div>"}]
