css:
table{
	width:500px !important;
}
th{
  max-width: 50px; /* Limit cell width to 50px */
  word-wrap: break-word; /* Enable word wrap within the cell */
  word-break: break-all; /* Forces wrap if the word is too long */
  text-align: center;
}
.headings {
    /*transform: rotate(-0.25turn);*/
    text-align: center;
    border: 1px solid black; 
    font-weight: bold;
    font-size: 10px;
    line-height:10px;

}

.page-headers{
    border: 1px solid black; 
    text-align:center;
    font-weight: bold;
    font-size:10px;
    line-height:2px;
}

.datas{
    border: 1px solid black; 
    line-height:0px;
    text-align:center;
    font-size:10px;
}

format_data:
[{"fieldname": "print_heading_template", "fieldtype": "Custom HTML", "options": "<div class="print-heading">	</div>"}, {"fieldtype": "Section Break", "label": ""}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "<div>
   {% set panels = get_panels(doc.name) %}
   {% set items = get_bundle_items(doc.name) %}
   {% set colours, colour_items = get_colours(doc.name,items) %}
   {% set arr_length = panels|length %}
   {% for colour in colours %}
        {% set items_length = colour_items[colour]|length %}
        {% if items_length > 30 %}
            {% set first_end = items_length/2 %}
            {% set second_end = first_end|int + first_end|int %}
        {% else %}
            {% set first_end = 15 %}
            {% set second_end = 30 %}
        {% endif %}
        {% if second_end < items_length %}
            {% set first_end = items_length/2 + 1 %}
            {% set second_end = first_end|int + first_end|int %}
        {% endif %}
        <table style="border-collapse: collapse; border: 1px solid black;page-break-inside:avoid;">
           <tr>
               <td colspan="{{ arr_length + 5 }}" style="padding-right: 0px !important;">
                   <table style="border-collapse: collapse;">
                       <tr>
                           <td class="page-headers" colspan="2">Lay No</td>
                           <td class="page-headers" colspan="{{ arr_length + 2}}">{{ doc.lay_no }}</td>
                       </tr>
                       <tr>
                           <td class="page-headers" colspan="2">Style</td>
                           <td class="page-headers" colspan="{{ arr_length + 2}}">{{ doc.item }}</td>
                       </tr>
                       <tr>
                           <th class="headings">B.No</th>
                           <th class="headings">Size</th>
                           <th class="headings">Qty</th>
                           <th class="headings">Shade</th>
                           {% for panel in panels %}
                               <th class="headings">
                                   {{panel}}
                                </th>
                           {% endfor %}
                       </tr>
                       {% set printed = {"print":0}%}
                       {% for item in colour_items[colour][:first_end|int] %}
                            {%if printed.update({"print":loop.index})%}{% endif %}
                           <tr>
                               <td class="datas">{{ item.bundle_no }}</td>
                               <td class="datas">{{ item.size }}</td>
                               <td class="datas">{{ item.quantity }}</td>
                               <td class="datas">{{ item.shade }}</td>
                               {% for panel in panels %}
                                   <td class="datas"></td>
                               {% endfor %}
                           </tr>
                       {% endfor %}
                       {% if printed.print < first_end|int %}
                            {% set empty_row_length = first_end|int - printed.print%}
                            {% for i in range(0,empty_row_length)%}
                                <tr>
                                    <td class="datas"></td>
                                   <td class="datas"></td>
                                   <td class="datas"></td>
                                   <td class="datas"></td>
                                   {% for panel in panels %}
                                       <td class="datas"></td>
                                   {% endfor %}
                                </tr>
                            {% endfor %}
                       {% endif %}
                   </table>
               </td>
               <td colspan="{{ arr_length + 5 }}" style="padding-left: 0px !important;">
                   <table style="border-collapse: collapse;">
                       <tr>
                           <td class="page-headers" colspan="2">Colour</td>
                           <td class="page-headers" colspan="{{ arr_length + 2}}">{{ colour }}</td>
                       </tr>
                       <tr>
                           <td class="page-headers" colspan="2">Lot No</td>
                           <td class="page-headers" colspan="{{ arr_length + 2}}">{{ doc.lot }}</td>
                       </tr>
                       <tr>
                           <th class="headings">B.No</th>
                           <th class="headings">Size</th>
                           <th class="headings">Qty</th>
                           <th class="headings">Shade</th>
                           {% for panel in panels %}
                               <th class="headings">
                                   {{panel}}
                                </th>
                           {% endfor %}
                       </tr>
                       {% set printed = {"print":0}%}
                       {% for item in colour_items[colour][first_end|int:second_end|int] %}
                           {%if printed.update({"print":loop.index})%}{% endif %}
                           <tr>
                               <td class="datas">{{ item.bundle_no }}</td>
                               <td class="datas">{{ item.size }}</td>
                               <td class="datas">{{ item.quantity }}</td>
                               <td class="datas">{{ item.shade }}</td>
                               {% for panel in panels %}
                                   <td class="datas"></td>
                               {% endfor %}
                           </tr>
                       {% endfor %}
                       {% if printed.print < first_end|int %}
                            {% set empty_row_length = first_end|int - printed.print%}
                            {% for i in range(0,empty_row_length)%}
                                <tr>
                                    <td class="datas"></td>
                                   <td class="datas"></td>
                                   <td class="datas"></td>
                                   <td class="datas"></td>
                                   {% for panel in panels %}
                                       <td class="datas"></td>
                                   {% endfor %}
                                </tr>
                            {% endfor %}
                       {% endif %}
                   </table>
               </td>
           </tr>
        </table>   
   {% endfor %}
</div>
"}]
