css:
table {
    width: 100%;
}
th{
  max-width: 50px; /* Limit cell width to 50px */
  word-wrap: break-word; /* Enable word wrap within the cell */
  word-break: break-all; /* Forces wrap if the word is too long */
  text-align: center;
}
.headings {
    /*transform: rotate(-0.25turn);*/
    text-align: center;
    border: 1px solid black; 
    font-weight: bold;
    font-size: 10px;
    line-height:10px;

}

.page-headers{
    border: 1px solid black; 
    text-align:center;
    font-weight: bold;
    font-size:10px;
    line-height:2px;
}

.datas{
    border: 1px solid black; 
    line-height:0px;
    text-align:center;
    font-size:10px;
}
