css:
.table-bordered {
    border-collapse: collapse;
    width: 100%;
}

.table-bordered td, .table-bordered th {
    border: 1px solid black; 
    padding: 4px;
    text-align: center;
}
.print-format td{
    padding:2px !important;
    font-size: 12px;
}
.print-format th{
    padding:2px !important;
    font-size: 12px;
}

format_data:
[{"fieldname": "print_heading_template", "fieldtype": "Custom HTML", "options": "<div>	</div>"}, {"fieldtype": "Section Break", "label": ""}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "<div>
    {% set panels, items, is_set_item = get_cutting_plan_laysheets_report(doc.name) %}
    {% if is_set_item %}
        {% for colour in items %}
            {% set splits = colour.split("-") %}
            {% set length = splits | length %}
            {% set part = splits[length - 1] %}
            <table class="table-bordered">
                {% set total_bundles = {} %}
                {% set total = {"total" : 0}%}
                {% set panel_length = panels[part] | length %}
                {% set col_width = 2 + panel_length - 1 %}
                {% set bundle_width = 2 %}
                {% if panel_length == 1%}
                    {% set col_width = 3 %}
                    {% set bundle_width = 1 %}
                {% endif %}

                <tr>
                    <th colspan="2"><strong>Lot No:</strong> {{doc.lot}}</th>
                    <th colspan="{{col_width}}"><strong>Style Name:</strong>  {{doc.item}}</th>
                    <th colspan="{{bundle_width}}"><strong>Colour: </strong> {{colour}}</th>
                </tr>
                <tr>
                    <td style="width:10%;"><strong> S.No</strong> </td>
                    <td style="width:10%;"><strong> Lay No</strong> </td>
                    <td style="width:10%;"><strong> Size</strong> </td>
                    <td style="width:10%;"><strong> Shade</strong> </td>
                    {% for panel in panels[part] %}
                        {% set _ = total_bundles.update({panel: 0}) %}
                        <td><strong> {{ panel }}</strong> </td>
                    {% endfor %}
                    <td style="width:15%;"><strong> No of Bundles</strong> </td>
                </tr>
                {% for row in items[colour] %}
                    <tr>
                        <td style="width:10%;">{{ loop.index }}</td>
                        <td style="width:10%;">{{ row.lay_no }}</td>
                        <td style="width:10%;">{{ row.size }}</td>
                        <td style="width:10%;">{{ row.shade }}</td>
                        {% set bundle = {"bundle": 0} %}
                        {% for panel in panels[part] %}
                            {% set panel_bundle = panel + "_Bundle" %}
                            {% if panel in row %}
                                {% if row[panel] > 0 %}
                                    {% set temp = row[panel_bundle] + total_bundles[panel] %}
                                    {% if total_bundles.update({panel : temp }) %}{% endif %}

                                    {% set total_temp = total['total'] + row[panel_bundle] %}
                                    {% if total.update({"total": total_temp}) %}{% endif %}

                                    <td>{{ row[panel] }}</td>
                                    {% set b = bundle["bundle"] + row[panel_bundle] %}
                                    {% if bundle.update({"bundle": b}) %}{% endif %}
                                {% else %}
                                    <td>0</td>
                                {% endif %}
                            {% else %}
                                <td>0</td>
                            {% endif %}    
                        {% endfor %}
                        <td style="width:15%;">{{ bundle["bundle"] }}</td>
                    </tr>
                {% endfor %}
                <tr>
                    <td style="width:10%;">Total</td>
                    <td style="width:10%;"></td>
                    <td style="width:10%;"></td>
                    <td style="width:10%;"></td>
                    {% for panel in panels[part] %}
                        <td>{{ total_bundles[panel] }}</td>
                    {% endfor %}
                    <td style="width:15%;">{{ total['total'] }}</td>
                </tr>
            </table>
        {% endfor %}
    {% else %}
        {% for colour in items %}
            <table class="table-bordered">
                {% set total_bundles = {} %}
                {% set total = {"total" : 0}%}
                {% set panel_length = panels | length %}
                {% set col_width = 2 + panel_length - 1 %}
                {% set bundle_width = 2 %}
                {% if panel_length == 1%}
                    {% set col_width = 3 %}
                    {% set bundle_width = 1 %}
                {% endif %}
                <tr>
                    <th colspan="2"><strong>Lot No:</strong> {{doc.lot}}</th>
                    <th colspan="{{col_width}}"><strong>Style Name:</strong>  {{doc.item}}</th>
                    <th colspan="{{bundle_width}}"><strong>Colour: </strong> {{colour}}</th>
                </tr>
                <tr>
                    <td style="width:10%;"><strong> S.No</strong> </td>
                    <td style="width:10%;"><strong> Lay No</strong> </td>
                    <td style="width:10%;"><strong> Size</strong> </td>
                    <td style="width:10%;"><strong> Shade</strong> </td>
                    {% for panel in panels %}
                        {% set _ = total_bundles.update({panel: 0}) %}
                        <td><strong> {{ panel }}</strong> </td>
                    {% endfor %}
                    <td style="width:15%;"><strong> No of Bundles</strong> </td>
                </tr>
                {% for row in items[colour] %}
                    <tr>
                        <td style="width:10%;">{{ loop.index }}</td>
                        <td style="width:10%;">{{ row.lay_no }}</td>
                        <td style="width:10%;">{{ row.size }}</td>
                        <td style="width:10%;">{{ row.shade }}</td>
                        {% set bundle = {"bundle": 0} %}
                        {% for panel in panels %}
                            {% set panel_bundle = panel + "_Bundle" %}
                            {% if panel in row %}
                                {% if row[panel] > 0 %}
                                    <td>{{ row[panel] }}</td>
                                    {% set b = bundle["bundle"] + row[panel_bundle] %}
                                    {% if bundle.update({"bundle": b}) %}{% endif %}

                                    {% set temp = row[panel_bundle] + total_bundles[panel] %}
                                    {% if total_bundles.update({panel : temp }) %}{% endif %}

                                    {% set total_temp = total['total'] + row[panel_bundle] %}
                                    {% if total.update({"total": total_temp}) %}{% endif %}
                                {% else %}
                                    <td>0</td>
                                {% endif %}
                            {% else %}
                                <td>0</td>        
                            {% endif %}
                        {% endfor %}
                        <td style="width:15%;">{{ bundle["bundle"] }}</td>
                    </tr>
                {% endfor %}
                <tr>
                    <td style="width:10%;">Total</td>
                    <td style="width:10%;"></td>
                    <td style="width:10%;"></td>
                    <td style="width:10%;"></td>
                    {% for panel in panels %}
                        <td>{{ total_bundles[panel] }}</td>
                    {% endfor %}
                    <td style="width:15%;">{{ total['total'] }}</td>
                </tr>
            </table>
        {% endfor %}    
    {% endif %}    
</div>"}]
