css:
.print-format td{
    padding: 0px !important;
}
table {
    border-collapse: collapse;
    width: 100%;
    font-family: Arial, sans-serif;
    font-size: 14px;
}

th, td {
    border: 1px solid #555;
    padding: 5px;
    text-align: center;
    vertical-align: middle;
}

th {
    background-color: #f2f2f2;
    font-weight: bold;
}


td > div {
    border-top: none;
    border-left: none;
    border-right: none;
}

td > div > div {
    border-bottom: 1px solid #555;
    display: flex;
    align-items: center;
    justify-content: center;
    background-color: #fff;
}

td > div > div:last-child {
    border-bottom: none;
}

tr:hover {
    background-color: #f9f9f9;
}

format_data:
[{"fieldname": "print_heading_template", "fieldtype": "Custom HTML", "options": "<div></div>"}, {"fieldtype": "Section Break", "label": ""}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "<div>
    {% set items = get_ironing_mistake_pf_items(doc.lot) %}
    <div style="text-align: center;">
        <h3>Ironing Mistake</h3>
    </div>
    <div style="font-size: medium;">
        <strong>Lot:</strong> {{doc.lot}}
    </div>
    <div style="font-size: medium;">
        <strong>Item:</strong> {{doc.item}}
    </div>
    <div style="font-size: medium;">
        <strong>Date:</strong> 
    </div>
    {% for item in items %}
        <table border="1" cellspacing="0" cellpadding="5" style="border-collapse: collapse; width: 100%;">
            <tr>
                {% for attr in item.attributes %} 
                    {% if attr != item.dependent_attribute and attr != item.stiching_attribute %}
                        <th>{{ attr }}</th>
                    {% endif %}    
                {% endfor %}
                <th>Types</th>
                {% for attr in item.primary_attribute_values %}
                    <th>{{ attr }}</th>
                {% endfor %}
                <th>Total Qty</th>
            </tr>
            {% for row in item['items'] %}
                <tr>
                    {% for attr in item.attributes %}
                        {% if attr != item.dependent_attribute and attr != item.stiching_attribute %}
                            {% if attr in row.attributes %}
                                <td style="text-align:center;">{{ row.attributes[attr] }}</td>
                            {% else %}
                                <td></td>
                            {% endif %}
                        {% endif %}
                    {% endfor %}
                    <td style="padding:0px; height:100px;">
                        <div style="display:flex; flex-direction:column; height:100%;">
                            <div style="flex:1;border:1px solid gray"> Adas </div>
                            <div style="flex:1;border:1px solid gray"> Oil </div>
                            <div style="flex:1;border:1px solid gray"> Misstitch</div>
                            <div style="flex:1;border:1px solid gray"> Others</div>
                        </div>
                    </td>
                    {% for attr in item.primary_attribute_values %}
                        <td style="padding:0px; height:100px;">
                            <div style="display:flex; flex-direction:column; height:100%;">
                                <div style="flex:1; width:50%; border:1px solid gray"></div>
                                <div style="flex:1; width:50%; border:1px solid gray"></div>
                                <div style="flex:1; width:50%; border:1px solid gray"></div>
                                <div style="flex:1; width:50%; border:1px solid gray"></div>
                            </div>
                        </td>
                    {% endfor %}
                    <td></td>
                </tr>
            {% endfor %}
        </table>
    {% endfor %}
</div>


"}]
