css:
.rework-header {
    margin-bottom: 12px;
    font-size: 10pt;
    line-height: 1.4;
}
.rework-header span {
    display: inline-block;
    margin-right: 18px;
    font-weight: 500;
}

table.rework-table {
    width: 100%;
    border-collapse: collapse;
    margin-bottom: 14px;
    font-size: 8pt;
}
.rework-table th, 
.rework-table td {
    border: 1px solid #444;
    text-align: center;
}
.rework-table th {
    background: #f1f1f1;
    font-weight: bold;
}
.expanded-row-content {
    padding: 8px;
    background: #fafafa;
}
.expanded-row-content h4 {
    margin: 0px 0 4px;
    font-size: 10pt;
    font-weight: bold;
    color: #333;
    border-bottom: 1px solid #ccc;
}

/* Inner Rework Tables */
.expanded-row-content table {
    width: 100%;
    border-collapse: collapse;
    margin-bottom: 5px;
}
.expanded-row-content th,
.expanded-row-content td {
    border: 1px solid #bbb;
    text-align: center;
    font-size: 9pt;
}
.expanded-row-content th {
    background: #f9f9f9;
}
.print-format{
    padding: 0.25in;
}
.print-format th{
    padding:0px !important;
}
.print-format td{
    padding:0px !important;
}
@media print {
    .rework-header { font-size: 9pt; }
    .rework-table th, .rework-table td { font-size: 9pt; }
    .expanded-row-content th, .expanded-row-content td { font-size: 8.5pt; }
}
