[{"fieldname": "print_heading_template", "fieldtype": "Custom HTML", "options": "<div></div>"}, {"fieldtype": "Section Break", "label": ""}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "<div>
    {% set items = fetch_order_item_details(doc.work_order_calculated_items,doc.production_detail) %}
    <table>
        {% for i in items%}
            <tr>
                {% if i['primary_attribute'] %}
                    <td>
                        <table>
                            {% set primary_values = {} %}
                            <tr>
                                <td>S.No.</td>
                                {% for j in i['final_state_attr'] %}
                                    <td>{{ j }}</td>
                                {% endfor %}
                                {% for j in i['primary_attribute_values'] %}
                                    {% set _ = primary_values.update({j:0}) %}
                                    <td>{{ j }}</td>
                                {% endfor %}
                            </tr>
                            {% for j in i['items'] %}
                                <tr>
                                    <td>{{ loop.index }}</td>
                                    {% for k in j['attributes'] %}
                                        <td>{{ j['attributes'][k] }}</td>
                                    {% endfor %}
                                    {% for k in j['values'] %}
                                        {% if j['values'][k] > 0%}
                                            {% set x = primary_values[k] + j['values'][k] %}
                                            {% if primary_values.update({k:x}) %}{% endif %}
                                            <td>{{ j['values'][k] }}</td>
                                        {% else %}
                                            <td>--</td>
                                        {% endif %}    
                                    {% endfor %}
                                </tr>
                            {% endfor %}
                            <tr>
                                <td>Total</td>
                                {% for j in i['final_state_attr'] %}
                                    <td></td>
                                {% endfor %}
                                {% for j in i['primary_attribute_values'] %}
                                    <td>{{ primary_values[j] }}</td>
                                {% endfor %}
                            </tr>
                        </table>
                    </td>
                {% else %}
                    <td>
                        <table>
                            <tr>
                                <td>S.No.</td>
                                {% for j in i['final_state_attr'] %}
                                    <td>{{ j }}</td>
                                {% endfor %}
                                <td>Quantity</td>
                            </tr>
                            {% for j in i['items'] %}
                                <tr>
                                    <td>{{ loop.index }}</td>
                                    {% for k in j['attributes'] %}
                                        <td>{{ k }}</td>
                                    {% endfor %}
                                    {% if j['values']['qty'] > 0 %}
                                        <td>{{ j['values']['qty'] }}</td>
                                    {% else %}
                                        <td>--</td>
                                    {% endif %}
                                </tr>
                            {% endfor %}
                        </table>
                    </td>
                {% endif %}
            </tr>
        {% endfor %}    
    </table>
</div>"}]
